mapper.select:

select rno, bnum, btitle, bcontent, id, bdate, bhits, bfile, bsfile 
	from (select rownum rno, bnum, btitle, bcontent, id, to_char(bdate,'yyyy/mm/dd') bdate, bhits, bfile, bsfile from  
		(select bnum, btitle, bcontent, id, bdate, bhits, bfile, bsfile from doppio_board 


           		where btitle like '%'||#{keyword}||'%'


           		where bcontent like '%'||#{keyword}||'%'


           		where id like '%'||#{keyword}||'%'


          order by bdate desc
         )          
    ) 
where rno between #{start} and #{end}

mapper.update:
update doppio_board 


		set btitle=#{btitle}, bcontent=#{bcontent}
					, bfile=#{bfile}, bsfile=#{bsfile}


		set btitle=#{btitle}, bcontent=#{bcontent}			


where bnum=#{bnum}
